mapper.select:
SELECT
    idx, category, title, description, teacherId, regDate, displayDate, status, price, thumbnail
FROM tbl_course
WHERE status = 'Y' AND teacherId = #{memberId}
ORDER BY regDate DESC
    LIMIT #{offset}, #{limit}

SELECT idx, title
FROM tbl_course
WHERE teacherId = #{teacherId};

mapper.update:
UPDATE tbl_course
SET status = 'N'
WHERE teacherId = #{teacherId} AND status = 'Y';

UPDATE tbl_course
SET deleteDate = #{deleteDate}, status = 'R'
WHERE idx = #{idx}

mapper.select:
SELECT idx
FROM tbl_course
WHERE deleteDate <= #{now}
AND status != 'D';

mapper.update:
UPDATE tbl_course
SET status = 'D'
WHERE idx = #{idx}

mapper.select:
SELECT idx, category, title, description, teacherId, regDate, displayDate, status, price, thumbnail FROM tbl_course
teacherId LIKE CONCAT('%', #{searchValue}, '%')
title LIKE CONCAT('%', #{searchValue}, '%')
price LIKE CONCAT('%', #{searchValue}, '%')
category LIKE CONCAT('%', #{searchValue}, '%')
status LIKE CONCAT('%', #{searchValue}, '%')
AND status = #{status}
${sortQuery}
ORDER BY regDate DESC
LIMIT #{offset}, #{limit}

SELECT idx, category, title, description, teacherId, regDate, displayDate, status, price, thumbnail
FROM tbl_course
WHERE idx = #{idx}

SELECT COUNT(*) FROM tbl_course
category = #{searchCategory}
AND ( title LIKE CONCAT('%', #{searchValue}, '%') OR teacherId LIKE CONCAT('%', #{searchValue}, '%') )

SELECT
idx, category, title, description, teacherId, regDate, displayDate, status, price, thumbnail
FROM tbl_course


        category = #{searchCategory}


        AND (
        title LIKE CONCAT('%', #{searchValue}, '%') OR
        teacherId LIKE CONCAT('%', #{searchValue}, '%')
        )


${sortQuery}
LIMIT #{offset}, #{limit}

SELECT idx, title
FROM tbl_course
WHERE idx = #{idx};

SELECT idx, `section`, courseIdx
FROM tbl_section
WHERE courseIdx = #{idx};

mapper.update:
UPDATE tbl_course
SET status = 'Y'
WHERE idx = #{courseIdx}

UPDATE tbl_course SET category = #{category}, title = #{title}, description = #{description}, displayDate = #{displayDate}, price = #{price},
thumbnail = #{thumbnail},
status = 'N', modifyDate = NOW() WHERE idx = #{idx}

UPDATE tbl_course
   SET status = 'K'
   WHERE idx=#{courseIdx}

