mapper.insert:
INSERT INTO tbl_member (
  memberId, pwd, name, phone, email, status, regDate, memberType
) VALUES (
           #{memberId}, #{pwd}, #{name}, #{phone}, #{email}, 'Y', NOW(), #{memberType}
         )

mapper.select:
SELECT memberId
FROM tbl_member
WHERE name = #{name}
  AND phone = #{phone}

SELECT pwd
FROM tbl_member
WHERE memberId = #{memberId}
  AND email = #{email}

SELECT memberId, name, phone, email, status, regDate, modifyDate, leaveDate, memberType
FROM tbl_member
WHERE memberId = #{memberId}

SELECT COUNT(*) > 0
FROM tbl_member
WHERE memberId = #{memberId}
